import React, { Component } from 'react'
import './App.css'
import axios from 'axios'

class App extends Component {

  constructor(props) {
    super(props);
      this.state = {
        selectedFile: null
      }

  }

  checkMimeType=(event)=>{
    //getting file object
    let files = event.target.files 
    //define message container
    let err = ''
    var x = 0
    // list allow mime type
    const types = ['image/png', 'image/jpeg', 'image/gif', 'video/mp4']
    // loop access array
    for(x; x<files.length; x++) {
    // compare file type find doesn't matach
    // eslint-disable-next-line
        if (types.every(type => files[x].type !== type)) {
        // create error message and assign to container   
        err += files[x].type+' is not a supported format\n';
        }
      };

    if (err !== '') { // if message not same old that mean has error 
      event.target.value = null // discard selected file
      console.log(err)
      return false; 
    }

    console.log("true")

    return true;

  }

  onChangeHandler=event=>{
    var files = event.target.files[0]
    if(this.checkMimeType(event)){ 
    // if return true allow to setState
      console.log("all right")
      this.setState({
      selectedFile: files
      })
    }
  }

  onClickHandler = () => {
    const data = new FormData()
    data.append('file', this.state.selectedFile)
    axios.post("http://localhost:8000/upload", data, { 
      // receive two    parameter endpoint url ,form data
    }).then(res => { // then print response status
      console.log(res.statusText)
    })
  }

  render() {
    return (

    <div class="container">
	    <div class="row">
	      <div class="col-md-6">
	        <form method="post" action="#" id="#">
            <div class="form-group files">
                <label>Upload Your File </label>
                <input type="file" class="form-control" name="file" multiple="" onChange={this.onChangeHandler}></input>
                <button type="button" class="btn btn-success btn-block" onClick={this.onClickHandler}>Upload</button>             </div>
          </form>
        </div>
	    </div>
  </div>
    )
  }
}

export default App
